function getEstimatedFinish(json, key)
{
    if (!json.hasOwnProperty(key)) return "--";
    else
    {
        let val = json[key];
        if (typeof val === "string")  return val;
        else if (typeof val === "object") return val.gui.export_value;
        else return "xcxc";
    }
}


